           body{
               background-color:#f9fafe !important;
           }
           .formdes-mb-5 {
               margin-bottom: 20px;
           }
           .formdes-pt-3 {
               padding-top: 12px;
           }
           .formdes-main-wrapper {
               height:100%;
               display: flex;
               align-items: center;
               justify-content: center;
               padding: 28px;
               background-color:#f3f6fd;
               border-radius:15px;
           }

           .formdes-form-wrapper {
               margin: 0 auto;
               max-width: 550px;
               width: 100%;
           }
           .formdes-form-label {
               display: block;
               font-weight: 500;
               font-size: 16px;
               color: #07074d;
               margin-bottom: 12px;
           }
           .formdes-form-label-2 {
               font-weight: 600;
               font-size: 20px;
               margin-bottom: 20px;
           }

           .formdes-form-input {
               width: 100%;
               padding: 12px 24px;
               border-radius: 6px;
               border: 1px solid #e0e0e0;
               background: white;
               font-weight: 500;
               font-size: 16px;
               color: #6b7280;
               outline: none;
               resize: none;
           }
           .formdes-form-input:focus {
               border-color: #3166e1;
               box-shadow: 0px 3px 8px rgba(0, 0, 0, 0.05);
           }

           .formdes-btn {
               text-align: center;
               font-size: 16px;
               border-radius: 6px;
               padding: 14px 32px;
               border: none;
               font-weight: 600;
               background-color: #3166e1;
               color: white;
               cursor: pointer;
           }
           .formdes-btn:hover {
               box-shadow: 0px 3px 8px rgba(0, 0, 0, 0.05);
           }

           .formdes--mx-3 {
               margin-left: -12px;
               margin-right: -12px;
           }
           .formdes-px-3 {
               padding-left: 12px;
               padding-right: 12px;
           }
           .flex {
               display: flex;
           }
           .flex-wrap {
               flex-wrap: wrap;
           }
           .w-full {
               width: 100%;
           }

           .formdes-file-input input {
               opacity: 0;
               position: absolute;
           }

           .formdes-file-input label {
               height:75%;
               position: relative;
               border: 3px dashed #c9d4e6;
               border-radius: 6px;
               min-height: 200px;
               display: flex;
               align-items: center;
               justify-content: center;
               padding: 48px;
               text-align: center;
           }
           .formdes-drop-file {
               display: block;
               font-weight: 600;
               color: #07074d;
               font-size: 20px;
               margin-bottom: 8px;
           }

           .formdes-or {
               font-weight: 500;
               font-size: 16px;
               color: #6b7280;
               display: block;
               margin-bottom: 8px;
           }
           .formdes-browse {
               font-weight: 500;
               font-size: 16px;
               color: #07074d;
               display: inline-block;
               padding: 8px 28px;
               border: 1px solid #e0e0e0;
               border-radius: 4px;
           }

           .formdes-file-list {
               border-radius: 6px;
               background: #f5f7fb;
               padding: 16px 32px;
           }

           .formdes-file-item {
               display: flex;
               align-items: center;
               justify-content: space-between;
           }

           .formdes-file-item button {
               color: #07074d;
               border: none;
               background: transparent;
               cursor: pointer;
           }

           .formdes-file-name {
               font-weight: 500;
               font-size: 16px;
               color: #07074d;
