      <script>
          const stepMenuOne = document.querySelector('.formbold-step-menu1')
          const stepMenuTwo = document.querySelector('.formbold-step-menu2')
          const stepMenuThree = document.querySelector('.formbold-step-menu3')

          const stepOne = document.querySelector('.formbold-form-step-1')
          const stepTwo = document.querySelector('.formbold-form-step-2')
          const stepThree = document.querySelector('.formbold-form-step-3')

          const formSubmitBtn = document.querySelector('.formbold-btn')
          const formBackBtn = document.querySelector('.formbold-back-btn')

          formSubmitBtn.addEventListener("click", function(event){
              event.preventDefault()
              if(stepMenuOne.className == 'formbold-step-menu1 active') {
                  event.preventDefault()

                  stepMenuOne.classList.remove('active')
                  stepMenuTwo.classList.add('active')

                  stepOne.classList.remove('active')
                  stepTwo.classList.add('active')

                  formBackBtn.classList.add('active')
                  formBackBtn.addEventListener("click", function (event) {
                      event.preventDefault()

                      stepMenuOne.classList.add('active')
                      stepMenuTwo.classList.remove('active')

                      stepOne.classList.add('active')
                      stepTwo.classList.remove('active')

                      formBackBtn.classList.remove('active')

                  })

              } else if(stepMenuTwo.className == 'formbold-step-menu2 active') {
                  event.preventDefault()

                  stepMenuTwo.classList.remove('active')
                  stepMenuThree.classList.add('active')

                  stepTwo.classList.remove('active')
                  stepThree.classList.add('active')

                  formBackBtn.classList.remove('active')
                  formSubmitBtn.textContent = 'Submit'
              } else if(stepMenuThree.className == 'formbold-step-menu3 active') {
                  document.querySelector('form').submit()
              }
          })
          function readURL(input) {
              if (input.files && input.files[0]) {
                  var reader = new FileReader();
                  reader.onload = function (e) {
                      $('#blah')
                          .attr('src', e.target.result)
                          .width(150)
                          .height(200);
                  };
                  reader.readAsDataURL(input.files[0]);
              }
          }
          function readURL(input) {
              if (input.files && input.files[0]) {
                  var reader = new FileReader();
                  reader.onload = function (e) {
                      document.getElementById('Changepic').style = "background-image: url("+e.target.result+");background-size: 100% 100%;";
                  };
                  reader.readAsDataURL(input.files[0]);
              }
          }
    function verify(){
        document.getElementById("tit").innerHTML = document.getElementById("title").value;

        document.getElementById("rec").innerHTML = document.getElementById("receiver").value;

        document.getElementById("whe").innerHTML = document.getElementById("when").value;

        document.getElementById("wher").innerHTML = document.getElementById("where").value;

        document.getElementById("desc").innerHTML = document.getElementById("pwd").value;
    }
      </script>




